angular.module('pogsUiApp').
  directive('approval', function(){
    return {
      restrict: 'E',
      scope: {
        genemodels: '=',
        update: '=',
      },
      controller: function ($scope, Params, Search, Plaza) {
        $scope.approved = false;
        $scope.unapproved = false;
        $scope.resolve = function () {
          if ($scope.genemodels.length < 1) {
            return;
          }
          Params.clear('approval');
          Params.set('approval', {
            tid: $scope.genemodels[0],
            type: 'byPOG',
            pogMethod: 'plaza_groups',
          });
          Search.query(Params.get('approval'), function (search) {
            var keys = Object.keys(search.results);
            Plaza.query({id: keys[0]}, function (plaza) {
              var plaza_genes = _.map(plaza.locus, function (val) {
                return val.genemodel
              });
              var diff = _.difference(plaza_genes, $scope.genemodels);

              if (diff.length < 1 && plaza_genes.length == $scope.genemodels.length) {
                $scope.approved = true;
              } else {
                $scope.unapproved = true;
              };
            });
          });
        };

        $scope.$watch('update', function () {
          $scope.resolve();
        });

      },
      template: '<div ng-show="approved" class="approved inline"><h5><i class="icon-ok-sign icon-1x text-success"></i> Predictions are consistent with Plaza</h5></div><div ng-show="unapproved" class="unapproved inline"><h5><i class="icon-ban-circle icon-1x text-error"></i> Predictions are inconsistent with Plaza</h5></div>',
      link: function ($scope, element, attr) {

      },
    };
});



angular.module('pogsUiApp')
  .directive('compile', function ($compile) {
  return function(scope, element, attrs) {
      scope.$watch(
        function(scope) {
           // watch the 'compile' expression for changes
          return scope.$eval(attrs.compile);
        },
        function(value) {
          // when the 'compile' expression changes
          // assign it into the current DOM
          element.html(value);

          // compile the new DOM and link it to the current
          // scope.
          // NOTE: we only compile .childNodes so that
          // we don't get into infinite loop compiling ourselves
          $compile(element.contents())(scope);
        }
    );
};
});


angular.module("pogsUiApp")
  .directive("scrollTo", function($window){
    return {
      restrict : "AC",
      compile : function(){

        var document = $window.document;
        
        function scrollInto(idOrName) {//find element with the give id of name and scroll to the first element it finds
          if(!idOrName)
            $window.scrollTo(0, 0);
          //check if an element can be found with id attribute
          var el = document.getElementById(idOrName);
          if(!el) {//check if an element can be found with name attribute if there is no such id
            el = document.getElementsByName(idOrName);

            if(el && el.length)
              el = el[0];
            else
              el = null;
          }

          if(el) //if an element is found, scroll to the element
            el.scrollIntoView();
          //otherwise, ignore
        }

        return function(scope, element, attr) {
          element.bind("click", function(event){
            scrollInto(attr.scrollTo);
          });
        };
      }
    };
});

angular.module('pogsUiApp').
  directive('treerender', function($rootScope, $location){
    return {
      restrict: 'E',
      scope: {
        genemodels: '=',
        tree: '=',
        url: '=',
        pogid: '=',
        divid: '@',
        color: '@',
        height: '@',
        width: '@',
        dy: '@',
        padding: '@',
        method: '@',
        lazy: '@',
      },
      transclude: true,
      template: '<small><p>Asterisks (*) mark members of this POG</p></small><div ng-transclude></div>',
      link: function (scope, element, attr) {
        
        var processTree = function (tree, cb) {
          var urlBase = scope.url;
          var $xml = angular.element(tree);
          var changeLength = function () {
            return $xml.find('branch_length').each(function() {
              angular.element(this).text('1');
            }).promise();
          }
          var changeAnnotation = function() {
            return $xml.find('name').each(function() {
              if (_.include(scope.genemodels, angular.element(this).text())) {
                var old_value = angular.element(this).text();
                angular.element(this).text(old_value + "*");
              } else {
                var search_method = "/";
                if (typeof scope.method != "undefined") {
                  search_method = "/" + scope.method + "/";
                }
                var addition = angular.element('<annotation><desc>Click to Search For ' + angular.element(this).text() + ' POG</desc><uri>'+ urlBase +'#/search' + search_method + 'genemodel/' + angular.element(this).text() + '</uri></annotation>');
                angular.element(this).parent().append(addition);
              }
            }).promise();
          }
          angular.element
          .when(changeLength(), changeAnnotation())
          .done(cb($xml[2].outerHTML));
        }

      var redraw = function (divid) {
        if (scope.divid == divid) {
          Smits.PhyloCanvas.Render.Style.line.stroke = scope.color;
          Smits.PhyloCanvas.Render.Style.text.fill = scope.color;
          Smits.PhyloCanvas.Render.Style.text.highlight = scope.color;
          Smits.PhyloCanvas.Render.Style.text["font-size"] = 12;
          if (scope.lazy == 'true') {
            draw();
          }
        }
      }
      var draw = function () {
          if (typeof scope.padding == 'undefined') {
            scope.padding = 100;
          } else {
            scope.padding = parseInt(scope.padding);
          }
          if (scope.genemodels.length > 0 && typeof scope.tree[scope.pogid] != 'undefined') {
            processTree(scope.tree[scope.pogid], function (tree) {
              scope.divId = 'phylo_' + scope.divid;
              var dataObject = {
                phyloxml: tree,
                fileSource: false 
              }
              angular.element('#phylo_' + scope.divid).html("");
              var elem = angular.element('#phylo_' + scope.divid);
              var render = new Smits.PhyloCanvas(dataObject,'phylo_' + scope.divid,parseInt(scope.width),parseInt(scope.height));
              angular.element('svg a').click(function (element) {
                window.location = element.delegateTarget.href.baseVal;
                return false;
              });
              angular.element('#phylo_' + scope.divid + '> svg').attr('height', parseInt(scope.height) + scope.padding);
              if (typeof scope.dy != "undefined") {
                var $ = angular.element;

                var unWatcher = scope.$watch(
                  function() {
                    return $('tspan').length != 0;
                  },
                  function() {

                    angular.element('tspan').attr('dy', '5');             

                    unWatcher();
                  }
               , true);
              }
            });
          }
        }
        if (scope.lazy != 'true') {
          scope.$watch('tree', draw);
        }
        $rootScope.$on('Flyout:redraw', function () {
          redraw("pog");
        });
        $rootScope.$on('Flyout:overlay:redraw', function () {
          redraw('plaza');
        });
      },
    };
});



angular.module('pogsUiApp').
  directive('atted', function(){
    return {
      restrict: 'E',
      scope: {
        locus: '=',
      },
      template: '<a class="btn btn-warning btn-mini" tooltip-placement="bottom" tooltip="Click to learn about the gene coexpression network for this Arabidopsis ortholog" target="_blank" href="http://atted.jp/cgi-bin/locus.cgi?loc={{locus}}">ATTED-II</a>',
      link: function (scope, element, attr) {
        scope.$watch('locus', function () {
          if (!scope.locus || !scope.locus.match(/()(AT[\w|\d]+)/)) {
            angular.element(element).hide();
          } else {
            angular.element(element).show();
          }
        });

      },
    };
});


